authorList = [ {"_id":"603374e0ff4a6578696d3a74","Name": "W. Frank Ableson", "Biography":{"About the Author": "Active in the mobile software marketplace, working with leading mobile companies including Handango, Airtime Management, and UnwiredTools. Managing Partner at Google", "Country":"Austria", "Age":35}, "Publication Count": 5},{"_id":"603374e0ff4a6578696d3a75","Name": "Charlie Collins", "Biography": {"About the Author":"mobile and web developer at MOVL, a contributor to several open source projects, and a coauthor of GWT in Practice and Unlocking Android. Former CEO at Twin Technologies LLC", "Country":"France", "Age":45} , "Publication Count": 4},{"_id":"603374e0ff4a6578696d3a76","Name": "Robi Sen", "Biography": {"About the Author":"Managing Partner at a startup company named Twin Technologies LLC, where he spends most of his time helping deliver next-generation Rich Internet Applications to Twin Technologies customers", "Country":"Spain", "Age":28 }, "Publication Count": 2},{"_id":"603374e0ff4a6578696d3a77","Name": "Gojko Adzic", "Biography":{"About the Author":"A UK based consultant, Gojko Adzic helps teams worldwide implement specification by example and agile testing practices. He has written two previous books on the subject and contributed to several open source projects supporting specification by example. Gojko is a frequent speaker at leading software development and testing conferences and runs the UK Agile Testing User Group.", "Country":"Ireland", "Age":48 }, "Publication Count": 1},{"_id":"603374e0ff4a6578696d3a78","Name": "Tariq Ahmed", "Biography":{"About the Author":"Tariq Ahmed is an accomplished web application pioneer having introduced next-generation web technologies to companies such as Bell Canada and Reuters. He and Jon Hirschi were first to introduce eBay to Adobe Flex which then later proliferated to other teams. As an Adobe Flex Community Expert, Tariq has been evangelizing the technology and supporting the community through various projects and is particularly known for his Community Flex (CFLEX.Net) site.","Country":"Ireland", "Age":48 }, "Publication Count": 6},{"_id":"603374e0ff4a6578696d3a79","Name": "Dan Orlando", "Biography":{"About the Author":"Daniel Orlando is a recognized RIA Architect, specializing in enterprise class Flex and AIR applications. Dan is often called on as a resource for information on top-ics involving bleeding edge technology platforms for radio interviews and printpublications, which include: PHP Architect magazine, Flex and Flash DeveloperMagazine, Amazon Web Services Developer Connection, Adobe DeveloperConnection, IBM developerWorks, PHPBuilder.com, and many others. Former CEO at Twin Technologies LLC,","Country":"Greece", "Age":53}, "Publication Count": 1},{"_id":"603374e0ff4a6578696d3a7a","Name": "John C. Bland II", "Biography":{"About the Author":" founder of Katapult Media Inc. which focuses on software and web development using technologies such as ColdFusion, the Flash Platform, PHP, Java, the .NET Platform, and Objective-C (iPhone, Mac OSX, etc). Through Katapult, he works diligently on custom software and web products for small and large clients throughout the world. As a 2009 Adobe Community Expert, John continues put back into the community which helped mold him into the developer he is today","Country":"Malta", "Age":63}, "Publication Count": 7},{"_id":"603374e0ff4a6578696d3a7b","Name": "Joel Hooks", "Biography":{"About the Author":"Joel Hooks is a Flash Platform developer with experience in Actionscript 3, Flex, and Python. With the introduction of Actionscript 3, Joel finally found a platform that allows him to architect useful tools while fully leveraging his experience as a visual artist. Joel is passionate about technology and enjoys exploring the landscape of frameworks, libraries, and tools that make his work constantly fun and challenging.","Country":"Malta", "Age":63}, "Publication Count": 9}, {"_id":"603374e0ff4a6578696d3a7c", "Name": "Levi Asher", "Biography":{"About the Author":"Levi Asher works as a technology manager for the New Media division of a major New York-based media/entertainment corporation, and spends his evenings trying to invent new creative literary forms using digital technology. He has created two well-known Web sites, Literary Kicks and Queensboro Ballads. Christian Crumlish is known on the Net as Debussy Fields, Dr. Chun, Hank Gurdjieff, Herbert Stencil, GATEFOLD SLEEVE, and other names. He is the editor and publisher of Enterzone, a hyper web text media zine art project, and he is working on his first novel.","Country":"United States", "Age":60}, "Publication Count": 2}, {"_id":"603374e0ff4a6578696d3a7d", "Name": "Christian Crumlish", "Biography":{"About the Author":"Christian Crumlish has been leading product and UX teams for more than two decades. He was head of product at 7cups.com and director of product management at CloudOn (sold to Dropbox). He is the founder of Design in Product, a consultancy and community of practice, and his clients and past employers include the PathCheck Foundation, Nest Health (Humanest) DataCoral, City Innovate, EduWorks, AIM, AOL, Yahoo, Extractable, Grateful Dead Productions, Rhino Records, Visa, and Sprint.","Country":"United States", "Age":43}, "Publication Count": 1}]


bookList = [{"_id": "6033736d137f3d8b50cc0942","Title" : "Unlocking Android", "ISBN" : "1933988673", "Page Count" : 416, "thumbnailUrl" : "https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/ableson.jpg", "Description" : {"Short Description": "Unlocking Android: A Developer's Guide provides concise, hands-on instruction for the Android operating system and development tools. This book teaches important architectural concepts in a straightforward writing style and builds on this with practical and useful examples throughout.", "Long Description": "Android is an open source mobile phone platform based on the Linux operating system and developed by the Open Handset Alliance, a consortium of over 30 hardware, software and telecom companies that focus on open standards for mobile devices. Led by search giant, Google, Android is designed to deliver a better and more open and cost effective mobile experience.    Unlocking Android: A Developer's Guide provides concise, hands-on instruction for the Android operating system and development tools. This book teaches important architectural concepts in a straightforward writing style and builds on this with practical and useful examples throughout. Based on his mobile development experience and his deep knowledge of the arcane Android technical documentation, the author conveys the know-how you need to develop practical applications that build upon or replace any of Androids features, however small.    Unlocking Android: A Developer's Guide prepares the reader to embrace the platform in easy-to-understand language and builds on this foundation with re-usable Java code examples. It is ideal for corporate and hobbyists alike who have an interest, or a mandate, to deliver software functionality for cell phones.    WHAT'S INSIDE:        * Android's place in the market      * Using the Eclipse environment for Android development      * The Intents - how and why they are used      * Application classes:            o Activity            o Service            o IntentReceiver       * User interface design      * Using the ContentProvider to manage data      * Persisting data with the SQLite database      * Networking examples      * Telephony applications      * Notification methods      * OpenGL, animation & multimedia      * Sample Applications  "} , "Status" : "PUBLISH", "Authors" : [ "603374e0ff4a6578696d3a74", "603374e0ff4a6578696d3a75", "603374e0ff4a6578696d3a76" ], "Categories" : [ "Open Source", "Mobile" ] },{"_id": "6033736d137f3d8b50cc0943","Title" : "Android in Action, Second Edition", "ISBN" : "1935182722", "Page Count" : 592,"thumbnailUrl" : "https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/ableson2.jpg", "Description" : {"Short Description": "Android in Action, Second Edition is a comprehensive tutorial for Android developers. Taking you far beyond \"Hello Android,\" this fast-paced book puts you in the driver's seat as you learn important architectural concepts and implementation strategies. You'll master the SDK, build WebKit apps using HTML 5, and even learn to extend or replace Android's built-in features by building useful and intriguing examples. ", "Long Description": "When it comes to mobile apps, Android can do almost anything   and with this book, so can you! Android runs on mobile devices ranging from smart phones to tablets to countless special-purpose gadgets. It's the broadest mobile platform available.    Android in Action, Second Edition is a comprehensive tutorial for Android developers. Taking you far beyond \"Hello Android,\" this fast-paced book puts you in the driver's seat as you learn important architectural concepts and implementation strategies. You'll master the SDK, build WebKit apps using HTML 5, and even learn to extend or replace Android's built-in features by building useful and intriguing examples. "}, "Status" : "PUBLISH", "Authors" : [ "603374e0ff4a6578696d3a74", "603374e0ff4a6578696d3a76" ], "Categories" : [ "Java" ] },{"_id": "6033736d137f3d8b50cc0944","Title" : "Flex 3 in Action", "ISBN" : "1933988746", "Page Count" : 576, "thumbnailUrl" : "https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/ahmed.jpg", "Description" : {"Short Description": "New web applications require engaging user-friendly interfaces   and the cooler, the better.", "Long Description": "With Flex 3, web developers at any skill level can create high-quality, effective, and interactive Rich Internet Applications (RIAs) quickly and easily. Flex removes the complexity barrier from RIA development by offering sophisticated tools and a straightforward programming language so you can focus on what you want to do instead of how to do it. And now that the major components of Flex are free and open-source, the cost barrier is gone, as well!    Flex 3 in Action is an easy-to-follow, hands-on Flex tutorial. Chock-full of examples, this book goes beyond feature coverage and helps you put Flex to work in real day-to-day tasks. You'll quickly master the Flex API and learn to apply the techniques that make your Flex applications stand out from the crowd.    Interesting themes, styles, and skins  It's in there.  Working with databases  You got it.  Interactive forms and validation  You bet.  Charting techniques to help you visualize data  Bam!  The expert Authors of Flex 3 in Action have one goal   to help you get down to business with Flex 3. Fast.    Many Flex books are overwhelming to new users   focusing on the complexities of the language and the super-specialized subjects in the Flex eco-system; Flex 3 in Action filters out the noise and dives into the core topics you need every day. Using numerous easy-to-understand examples, Flex 3 in Action gives you a strong foundation that you can build on as the complexity of your projects increases."}, "Status" : "PUBLISH", "Authors" : [ "603374e0ff4a6578696d3a78"], "Categories" : [ "Internet" ] },{"_id":"6033736d137f3d8b50cc0945", "Title" : "Coffeehouse", "ISBN" : "1884777384", "Page Count" : 316, "thumbnailUrl" : "https://s3.amazonaws.com/AKIAJC5RLADLUMVRPFDQ.book-thumb-images/asher.jpg", "Description":{"Short Description":"To read about stories, poems and essays originally published on the World Wide Web.", "Long Description":"Coffeehouse is an anthology of stories, poems and essays originally published on the World Wide Web. The purpose is to capture the zeitgeist of the web's creative community, and to give readers a chance to enjoy some of the best and most notable original works that have appeared in this form. It showcases over forty individual web writers, among them Joseph Squier, Martha Conway, Jason Snell, David Alexander, Carl Steadman and Walter Miller. The intent is to show the variety and vitality of the web's blossoming literary \"scene,\" and to capture the unique and highly iconoclastic \"personality\" of the web community."}, "Status" : "PUBLISH", "Authors" : [ "603374e0ff4a6578696d3a7c", "603374e0ff4a6578696d3a7d" ], "Categories" : [ "Miscellaneous" ] }]
